# Given a string, find the length of the longest substring without repeating characters


def longest_substring(string):
    current_max = 0
    for i, letter in enumerate(string):
        sub = string[i]
        check = string[i+1:len(string)]
        for j, letter_c in enumerate(check):
            if letter_c not in sub and j <= len(sub) - 1:
                sub += letter_c
        current_max = max(current_max, len(sub))
    return current_max


print(longest_substring('abbabcbb') == 3)
print(longest_substring('bu') == 2)
print(longest_substring('pwwkew') == 3)
